package Week1.day4;

public class SumofSeries {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sumResult = 0;
		for (int i = 1; i <= 5; i++) {
			sumResult = sumResult + i;//0+1=1, 1+2=3,3+3=6,6+4=10,10+5=15
		}
		System.out.println("Sum of Series: "+sumResult);
	}

}
